import { Component } from '@angular/core';

@Component({
  selector: 'testing',
  templateUrl: './test.component.html',
  styleUrls: ['./test.component.css']
})
export class TestComponent {
   name: string = "ladzani";
   textColor = false;
}
<div>
  <p>hello, <span [class.text-color]="textColor">{{ name }}</span></p>
  <button >click me</button>
</div>
