import { Component } from '@angular/core';

@Component({
  selector: 'testing',
  templateUrl: './test.component.html',
  styleUrls: ['./test.component.css']
})
export class TestComponent {
   name: string = "ladzani";
   textColor = false;
   cFont = "control-font";
  display = "";

   changeColor(){
    this.textColor = !this.textColor;
   }

   clicked(num: object, value: HTMLInputElement){
    this.display = value.value;
    console.log(num);
   }
}
<div>
  <p>hello, <span [class]="cFont" [class.text-color]="textColor">{{ name }}</span></p>
  <button (click)="changeColor()">click me</button>

  <div>
      <ul ngIf="displayList; else myFirstTemplate">
        <li>drugs store</li>
        <li>food store</li>
      </ul>
      <ng-template #myFirstTemplate>
        <ul>
        <li>NO drugs found</li>
        <li>All i wanted was to be a hacker!</li>
      </ul>
    </ng-template>
  </div>
  <div>

  </div>
</div>
