import { Component } from '@angular/core';

@Component({
  selector: 'testing',
  templateUrl: './test.component.html',
  styleUrls: ['./test.component.css']
})
export class TestComponent {
   name: string = "ladzani";
   textColor = false;
   cFont = "control-font";
  display = "";
   changeColor(){
    this.textColor = !this.textColor;
   }

   clicked(num: object, value: string){
    this.display = value.value;
    console.log(num);
   }
}
<div>
  <p>hello, <span [class]="cFont" [class.text-color]="textColor">{{ name }}</span></p>
  <button (click)="changeColor()">click me</button>
</div>
<div>
  <p>under test still</p>
  <input #myInput type="text">
  <button (click)="clicked($event, myInput)">run tests</button>
  {{display}}


</div>
