import { Component } from '@angular/core';

@Component({
  selector: 'testing',
  templateUrl: './test.component.html',
  styleUrls: ['./test.component.css']
})
export class TestComponent {
   name: string = "ladzani";
   textColor = false;
   cFont = "control-font";
  display = "";



displayList = false;





   changeColor(){
    this.textColor = !this.textColor;
    this.displayList = !this.displayList;
   }

   clicked(num: object, value: HTMLInputElement){
    this.display = value.value;
    console.log(num);
   }
}
<div>
  <p>
    hello,
    <span [class]="cFont" [class.text-color]="textColor">{{ name }}</span>
  </p>
  <button (click)="changeColor()">click me</button>

  <div *ngIf="displayList; then nestedTemplate; else mySecondTemplate">

    <ng-template #mySecondTemplate>
      <div>
        this is my second template! <br />
        first one didn't rund because i forgot to add an *
      </div>
    </ng-template>
  </div>
  <ng-template #nestedTemplate
      ><ul>
        <li>the dark web</li>
        <li>the second dark web</li>
        <li>third dark web broski!!!</li>
      </ul></ng-template
    >
</div>
