import { Component } from '@angular/core';

@Component({
  selector: 'testing',
  templateUrl: './test.component.html',
  styleUrls: ['./test.component.css']
})
export class TestComponent {
   name: string = "ladzani";
   textColor = false;
   cFont = "control-font";
  display = "";



displayList = false;





   changeColor(){
    this.textColor = !this.textColor;
   }

   clicked(num: object, value: HTMLInputElement){
    this.display = value.value;
    console.log(num);
   }
}
<div>
  <p>hello, <span [class]="cFont" [class.text-color]="textColor">{{ name }}</span></p>
  <button (click)="changeColor()">click me</button>

<div *ngIf="displayList; else mySecondTemplate">

</div>
<ng-template #mySecondTemplate>
  <div>this is my second template! <br> </div>
</ng-template>
</div>
