import { Component, OnInit } from '@angular/core';
import { TestserviceService } from '../testservice.service';
import { StrangePeople } from '../../interfaces/StrangePeople';


@Component({
  selector: 'testing',
  templateUrl: './test.component.html',
  styleUrls: ['./test.component.css']
})

export class TestComponent implements OnInit {

public people: any = [];
public errorVariable = "";
public isDisabled: boolean = true;

public testTheButton = "testButton2";
public buttonColor: boolean = true;
public hello = "";

constructor(private testService: TestserviceService){

}

 ngOnInit(): void {
  this.testService.getPeople().subscribe( (data) => this.people = data, (error) => this.errorVariable = error);
 }



 sayHello(){
  this.hello = "hello, there how are you?";  
 }

}






<!--property binding-->
<input [disabled]="isDisabled" type="text">

<!--class binding-->
<button class="testButton1">test button 1</button>
<button [class]="testTheButton">test button 2</button>
<button [class.testButton2]="buttonColor">test button 3</button>
<button []="buttonColor">test button 3</button>

<!--event binding-->
<button (click)="sayHello()">greet</button>
<div>{{hello}}</div>

<!-- interpolation, piping and the ngFor structual directive-->
<div>{{ errorVariable }}</div>
<div  id="container">
  <div *ngFor="let person of people">
    <div class="card">
      <h1>{{ person.name | uppercase }}</h1>
      <hr>
      <br>
      <p>{{ person.description }}</p>
      <br>
      <small>{{ person.hireDate | date:"short" }}</small>
    </div>
  </div>
</div>
