import { Component } from '@angular/core';

@Component({
  selector: 'testing',
  templateUrl: './test.component.html',
  styleUrls: ['./test.component.css']
})
export class TestComponent {
   name: string = "ladzani";
   textColor = false;
   cFont = "control-font";
  display = "";
   changeColor(){
    this.textColor = !this.textColor;
   }

   clicked(num){
    this.display = "<div>displayed becaused you have clicked the button</div>";
    console.log(num);
   }
}
<div>
  <p>hello, <span [class]="cFont" [class.text-color]="textColor">{{ name }}</span></p>
  <button (click)="changeColor()">click me</button>
</div>
<div>
  <p>under test still</p>
  <button (click)="clicked($num)">run tests</button>
  {{display}}
</div>
