import { Component, EventEmitter, Input, Output } from '@angular/core';

@Component({
  selector: 'testing',
  templateUrl: './test.component.html',
  styleUrls: ['./test.component.css']
})
export class TestComponent {
   public name: string = "ladzani";
   @Input() public pd: string = "";

   @Output() public emitMessageToParent = new EventEmitter();

   trigger(){
    this.emitMessageToParent.emit("sending data from childComponent to parentComponenet is hard!");
   }
}
<div>
  <p>
    hello, <span>{{ name }}</span>
  </p>
  <p>this text comes from the AppComponent: {{ pd }}</p>
  <button (click)="trigger()">writeMessage</button>
</div>
