import { Component } from '@angular/core';

@Component({
  selector: 'testing',
  templateUrl: './test.component.html',
  styleUrls: ['./test.component.css']
})
export class TestComponent {
   name: string = "ladzani";
   textColor = false;
   cFont = "control-font";
  display = "";
   changeColor(){
    this.textColor = !this.textColor;
   }

   clicked(num: object){
    this.display = "<div>displayed becaused you have clicked the button</div>";
    console.log(num);
   }
}
